// Simple script to cycle all icons if opened standalone
// TODO: Darkmode background, right click to reverse
window.addEventListener("load", () => {
	let ids = []
	document.querySelectorAll("#icons symbol").forEach(el => ids.push(el.id))
	let pointer = 0
	function nextIcon() {
		let id = ids[pointer++]
		if (pointer >= ids.length) pointer = 0
		document.getElementById("demo-icon").setAttribute("href", "#" + id)
		document.getElementById("demo-title").innerHTML = id
	}
	window.addEventListener("click", nextIcon)
	nextIcon()
})
